import Feriado from "../../feriado";

export const NewYearsDay = new Feriado(1, 1, "New Year's Day");
export const SaoPauloAnniversary = new Feriado(
  25,
  1,
  "Sao Paulo's Anniversary"
);
export const TiradentesDay = new Feriado(21, 4, "Tiradentes Day");
export const LaborDay = new Feriado(1, 5, "Labor Day");
export const RevolutionDay = new Feriado(9, 7, "Revolution Day");
export const IndependenceDay = new Feriado(7, 9, "Independence Day");
export const NossaSraAparecidaDay = new Feriado(
  12,
  10,
  "Nossa Senhora Aparecida's Day"
);
export const AllSoulsDay = new Feriado(2, 11, "All Souls Day");
export const RepublicDay = new Feriado(15, 11, "Republic Day");
export const BlackConciousnessDay = new Feriado(
  20,
  11,
  "Black Conciousness' Day"
);
export const ChristmasEve = new Feriado(24, 12, "Christmas Eve");
export const Christmas = new Feriado(25, 12, "Christmas");

export function CarnivalMonday(domingoDePascoa) {
  const feriado = new Feriado();
  return feriado.ObterFeriadoComIntervaloFixo(
    domingoDePascoa,
    -48,
    "Carnival Monday"
  );
}

export function CarnivalTuesday(domingoDePascoa) {
  const feriado = new Feriado();
  return feriado.ObterFeriadoComIntervaloFixo(
    domingoDePascoa,
    -47,
    "Carnival Tuesday"
  );
}

export function PassionOfChrist(domingoDePascoa) {
  const feriado = new Feriado();
  return feriado.ObterFeriadoComIntervaloFixo(
    domingoDePascoa,
    -2,
    "Passion of Christ"
  );
}

export function CorpusChristi(domingoDePascoa) {
  const feriado = new Feriado();
  return feriado.ObterFeriadoComIntervaloFixo(
    domingoDePascoa,
    60,
    "Corpus Christi"
  );
}
