import { addDays, getDay, getMonth } from "date-fns";

export default class Feriado {
  constructor(dia, mes, descricao) {
    this.dia = dia;
    this.mes = mes;
    this.descricao = descricao;
  }

  obterFeriadoComIntervaloFixo(domingoDePascoa, intervalo, descricao) {
    let diaAdicionado = domingoDePascoa.addDays(intervalo).getDay();
    let mesAdicionado = domingoDePascoa.addDays(intervalo).getMonth();

    return { diaAdicionado, mesAdicionado, descricao };
  }
}
