import { addDays, getDay, getMonth } from "date-fns";

export default function feriado() {
  function feriadoFixo(dia, mes, descricao) {
    return {
      dia,
      mes,
      descricao,
    };
  }

  function feriadoComIntervaloFixo(domingoDePascoa, intervalo, descricao) {
    let dia = getDay(addDays(new Date(domingoDePascoa), intervalo));
    let mes = getMonth(addDays(new Date(domingoDePascoa), intervalo));

    return {
      dia,
      mes,
      descricao,
    };
  }

  return {
    feriadoFixo,
    feriadoComIntervaloFixo,
  };
}

