import { addDays, getDay, getMonth } from "date-fns";
import { obterDomingoDePascoa } from "./calendariosLunares/gregoriano";

export default class Feriado {
  constructor(dia, mes, descricao) {
    this.dia = dia;
    this.mes = mes;
    this.descricao = descricao;
  }

  obterFeriadoComIntervaloFixo(a, intervalo, descricao) {
    let domingoDePascoa = obterDomingoDePascoa(2020);

    let diaAdicionado = getDay(addDays(new Date(domingoDePascoa), intervalo));
    let mesAdicionado = getMonth(addDays(new Date(domingoDePascoa), intervalo));

    console.log(diaAdicionado, mesAdicionado);

    return new Feriado(diaAdicionado, mesAdicionado, descricao);
  }
}
