import Feriado from "../../feriado";

export default class Feriados {
  constructor() {}

  NewYearsDay = new Feriado(1, 1, "New Year's Day");
  SaoPauloAnniversary = new Feriado(25, 1, "Sao Paulo's Anniversary");
  TiradentesDay = new Feriado(21, 4, "Tiradentes Day");
  LaborDay = new Feriado(1, 5, "Labor Day");
  RevolutionDay = new Feriado(9, 7, "Revolution Day");
  IndependenceDay = new Feriado(7, 9, "Independence Day");
  NossaSraAparecidaDay = new Feriado(12, 10, "Nossa Senhora Aparecida's Day");
  AllSoulsDay = new Feriado(2, 11, "All Souls Day");
  RepublicDay = new Feriado(15, 11, "Republic Day");
  BlackConciousnessDay = new Feriado(20, 11, "Black Conciousness' Day");
  ChristmasEve = new Feriado(24, 12, "Christmas Eve");
  Christmas = new Feriado(25, 12, "Christmas");

  CarnivalMonday(domingoDePascoa) {
    return Feriado.ObterFeriadoComIntervaloFixo(
      domingoDePascoa,
      -48,
      "Carnival Monday"
    );
  }
  CarnivalTuesday(domingoDePascoa) {
    return Feriado.ObterFeriadoComIntervaloFixo(
      domingoDePascoa,
      -47,
      "Carnival Tuesday"
    );
  }
  PassionOfChrist(domingoDePascoa) {
    return Feriado.ObterFeriadoComIntervaloFixo(
      domingoDePascoa,
      -2,
      "Passion of Christ"
    );
  }
  CorpusChristi(domingoDePascoa) {
    return Feriado.ObterFeriadoComIntervaloFixo(
      domingoDePascoa,
      60,
      "Corpus Christi"
    );
  }
}
