import { addDays, getDay, getMonth } from "date-fns";

export class feriado {
  constructor(dia, mes, descricao) {
    let feriado = {};
    feriado.dia = dia;
    feriado.mes = mes;
    feriado.descricao = descricao;
    obterFeriadoComIntervaloFixo(domingoDePascoa, intervalo, descricao);
    {
      return new Feriado(
        domingoDePascoa.addDays(intervalo).getDay(),
        domingoDePascoa.addDays(intervalo).getMonth(),
        descricao
      );
    }
    feriado.obterFeriadoComIntervaloFixo = obterFeriadoComIntervaloFixo;
    return feriado;
  }
}

export default class Feriado {
  constructor(dia, mes, descricao) {
    this.dia = dia;
    this.mes = mes;
    this.descricao = descricao;
  }

  ObterFeriadoComIntervaloFixo(domingoDePascoa, intervalo, descricao) {}
}
